import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Import CSS for styling

// Define your API key as a constant
const API_KEY = 'XFXyFO6PhxoNZ7Q5GW9aplmrRfeolgff';

function App() {
  const [city, setCity] = useState('Toronto'); // Default city is set to 'Toronto'
  const [weatherData, setWeatherData] = useState(null);

  const fetchWeatherData = async (cityName) => {
    try {
      const response = await axios.get(`https://api.tomorrow.io/v4/weather/realtime?location=${encodeURIComponent(cityName)}&apikey=${API_KEY}`);
      setWeatherData(response.data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  const handleInputChange = (event) => {
    setCity(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    fetchWeatherData(city);
  };

  useEffect(() => {
    // Fetch initial weather data for the default city (Toronto)
    fetchWeatherData('Toronto');
  }, []);

  return (
    <div>
      <h1>Real-Time Weather Data</h1>
      <h2>City: {weatherData && weatherData.location.name}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter city name"
          value={city}
          onChange={handleInputChange}
        />
        <button type="submit">Get Weather</button>
      </form>
      {weatherData ? (
        <div className="weather-card">
          <p className="weather-info">Time: {weatherData.data.time}</p>
          <p className="weather-info">Temperature: {weatherData.data.values.temperature}Â°C</p>
          <p className="weather-info">Humidity: {weatherData.data.values.humidity}%</p>
          <p className="weather-info">Cloud Cover: {weatherData.data.values.cloudCover}%</p>
          <p className="weather-info">Wind Speed: {weatherData.data.values.windSpeed} m/s</p>
          <p className="weather-info">UV Index: {weatherData.data.values.uvIndex}</p>
          <p className="weather-info">Visibility: {weatherData.data.values.visibility} km</p>
          {/* Add more weather data fields here */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default App;
