import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Import CSS for styling
import Marquee from 'react-fast-marquee'; // Import Marquee component

// Define your API key as a constant
const API_KEY = 'XFXyFO6PhxoNZ7Q5GW9aplmrRfeolgff';

function App() {
  const defaultCities = ['Mumbai', 'Delhi', 'Chennai', 'Kolkata'];
  const [cities, setCities] = useState(defaultCities);
  const [weatherData, setWeatherData] = useState({});
  const [inputCity, setInputCity] = useState('');

  // Function to fetch weather data based on coordinates
  const fetchWeatherDataByCoordinates = async (latitude, longitude) => {
    try {
      const response = await axios.get(`https://api.tomorrow.io/v4/weather/realtime?location=${latitude},${longitude}&apikey=${API_KEY}`);
      const cityName = response.data.data.location.city;
      setWeatherData((prevData) => ({ ...prevData, [cityName]: response.data }));
    } catch (error) {
      console.error('Error fetching weather data for coordinates:', error);
    }
  };

  useEffect(() => {
    // Fetch weather data for default cities
    defaultCities.forEach((city) => fetchWeatherData(city));

    // Fetch weather data based on user's current location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        fetchWeatherDataByCoordinates(latitude, longitude);
      }, (error) => {
        console.error('Error getting user location:', error);
      });
    } else {
      console.error('Geolocation is not supported by this browser.');
    }
  }, []);

  const handleCityChange = (event) => {
    setInputCity(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission based on city name if needed
    console.log("Form submitted:", inputCity);
  };

  return (
    <div className="app-container">
      <header>
        <h1>Weather Dashboard</h1>
        <p>Real-Time Weather Information</p>
      </header>
      <div className="input-city">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Enter city name"
            value={inputCity}
            onChange={handleCityChange}
          />
          <button type="submit">Get Weather</button>
        </form>
        <div style={{ display: "flex", justifyContent: "center", padding: "10px" }}>
          {inputCity && weatherData[inputCity] && (
            <div className="weather-input1">
              <h3>{inputCity}</h3>
              <p>Temperature: {weatherData[inputCity].data.values.temperature}°C</p>
              <p>Humidity: {weatherData[inputCity].data.values.humidity}%</p>
              <p>Cloud Cover: {weatherData[inputCity].data.values.cloudCover}%</p>
              <p>Wind Speed: {weatherData[inputCity].data.values.windSpeed} m/s</p>
              <p>UV Index: {weatherData[inputCity].data.values.uvIndex}</p>
              <p>Visibility: {weatherData[inputCity].data.values.visibility} km</p>
            </div>
          )}
        </div>
      </div>
      <div className="default-cities">
        <Marquee gradient={false} speed={100}>
          <div className="weather-container">
            {defaultCities.map((city, index) => (
              <div key={index} className="weather-card">
                <h3>{city}</h3>
                {weatherData[city] ? (
                  <div>
                    <p>Temperature: {weatherData[city].data.values.temperature}°C</p>
                    <p>Humidity: {weatherData[city].data.values.humidity}%</p>
                    <p>Cloud Cover: {weatherData[city].data.values.cloudCover}%</p>
                    <p>Wind Speed: {weatherData[city].data.values.windSpeed} m/s</p>
                    <p>UV Index: {weatherData[city].data.values.uvIndex}</p>
                    <p>Visibility: {weatherData[city].data.values.visibility} km</p>
                  </div>
                ) : (
                  <p>Loading...</p>
                )}
              </div>
            ))}
          </div>
        </Marquee>
      </div>
    </div>
  );
}

export default App;
