import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Import CSS for styling
import Marquee from 'react-fast-marquee'; // Import Marquee component

const API_KEY = 'XFXyFO6PhxoNZ7Q5GW9aplmrRfeolgff';

function App() {
  const defaultCities = ['Mumbai', 'Delhi', 'Chennai', 'Kolkata'];
  const [cities, setCities] = useState(defaultCities);
  const [weatherData, setWeatherData] = useState({});
  const [inputCity, setInputCity] = useState('');

  useEffect(() => {
    fetchDefaultWeatherData();
  }, []);

  const fetchDefaultWeatherData = async () => {
    try {
      const promises = defaultCities.map(city => fetchWeatherData(city));
      await Promise.all(promises);
    } catch (error) {
      console.error('Error fetching default weather data:', error);
    }
  };

  const fetchWeatherData = async (cityName) => {
    try {
      const response = await axios.get(`https://api.tomorrow.io/v4/weather/realtime?location=${encodeURIComponent(cityName)}&apikey=${API_KEY}`);
      setWeatherData(prevData => ({ ...prevData, [cityName]: response.data }));
    } catch (error) {
      console.error('Error fetching weather data for', cityName, ':', error);
    }
  };

  const handleCityChange = (event) => {
    setInputCity(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (inputCity.trim() !== '') {
      fetchWeatherData(inputCity);
      setInputCity('');
    }
  };

  const renderWeatherInfo = (city) => {
    const cityData = weatherData[city]?.data?.values;
    if (cityData) {
      return (
        <div className="weather-card">
          <h3>{city}</h3>
          <p>Temperature: {cityData.temperature}°C</p>
          <p>Humidity: {cityData.humidity}%</p>
          <p>Cloud Cover: {cityData.cloudCover}%</p>
          <p>Wind Speed: {cityData.windSpeed} m/s</p>
          <p>UV Index: {cityData.uvIndex}</p>
          <p>Visibility: {cityData.visibility} km</p>
        </div>
      );
    } else {
      return <p>Loading...</p>;
    }
  };

  return (
    <div className="app-container">
      <header>
        <h1>Weather Dashboard</h1>
        <p>Real-Time Weather Information</p>
      </header>
      <div className="input-city">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Enter city name"
            value={inputCity}
            onChange={handleCityChange}
          />
          <button type="submit">Get Weather</button>
        </form>
        <div className="weather-input">
          {inputCity && weatherData[inputCity] && renderWeatherInfo(inputCity)}
        </div>
      </div>
      <div className="default-cities">
        <Marquee gradient={false} speed={100}>
          <div className="weather-container">
            {defaultCities.map(city => (
              <div key={city} className="weather-card">
                {renderWeatherInfo(city)}
              </div>
            ))}
          </div>
        </Marquee>
      </div>
    </div>
  );
}

export default App;
